"use strict";
const
  {Tools} = require('mbeeg')
  , {ipcMain, ipcRenderer} = require('electron')
;

class Server {
  constructor(configFile){
    this.conf = Tools.loadConfiguration(configFile);
    ipcMain.on();
  
  }
  
  _fetch  (obj, pathArray){
    return this._fetch(obj[pathArray[0]], pathArray.slice(1));
  }
  
  get(...path){
      ipcMain.send('config-get', path);
  }
  
  set(value, ...path){
      ipcMain.send('config-get', path);
  }
  
}

class Client{
  constructor(configFile){
    this.conf = Tools.loadConfiguration(configFile);
  }
  get(){
    ipcRenderer.send('config-get', path);
  }
  
  set(){
    ipcRenderer.send('config-get', path);
  }
  
}

module.exports = {
  Server: Server,
  Client: Client
};
